.stat-icon i {
    width: 20px;
    height: 20px;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    color: var(--text-secondary);
    letter-spacing: 0;
}

.stat-value {
    font-size: 32px;
    font-weight: var(--font-bold);
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 16px 0;
}

.violations {
    list-style: none;
    padding: 0;
    margin: 0;
}

.violation-item {
    display: flex;
    align-items: flex-start;
    gap: 12px;
    padding: 12px;
    margin-bottom: 8px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    transition: all var(--transition-fast);
    border: 1px solid var(--border-subtle);
}

.violation-item:hover {
    background-color: var(--bg-hover);
    transform: translateX(4px);
    box-shadow: var(--shadow-sm);
}

.violation-item-icon {
    flex-shrink: 0;
}

.violation-item-text {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.violation-severity {
    font-size: 14px;
    font-weight: var(--font-medium);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: var(--text-secondary);
}

.violation-description {
    font-size: 16px;
    font-weight: var(--font-regular);
    color: var(--text-primary);
    line-height: 1.4;
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-context {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
    }

    .viobox {
        width: 90%;
        max-width: 557px;
    }
}

/* ========================================
   Tooltip System
   ======================================== */
.tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 12px;
    background-color: var(--text-primary);
    color: var(--text-white);
    border-radius: 8px;
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: var(--z-dropdown);
    box-shadow: var(--shadow-lg);
}

.tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: var(--text-primary);
}

.tooltip-mode .tips-toggle:hover .tooltip {
    opacity: 1;
}

/* VIObox Tooltips */
.viobox-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 16px;
    background-color: var(--text-primary);
    color: var(--text-white);
    border-radius: 8px;
    font-size: 14px;
    font-weight: var(--font-regular);
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: var(--z-dropdown);
    box-shadow: var(--shadow-lg);
}

.viobox-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: var(--text-primary);
}

.tooltip-mode .viobox:hover .viobox-tooltip {
    opacity: 1;
}

