// performance-monitor.js
// Tracks and reports page load performance

(function() {
  'use strict';

  // Wait for page load to complete
  window.addEventListener('load', function() {
    // Get performance timing data
    const perfData = performance.timing;
    const loadTime = perfData.loadEventEnd - perfData.navigationStart;
    const domReadyTime = perfData.domContentLoadedEventEnd - perfData.navigationStart;
    const renderTime = perfData.domComplete - perfData.domLoading;

    console.log('=== Performance Report ===');
    console.log(`Total Page Load Time: ${loadTime}ms`);
    console.log(`DOM Ready Time: ${domReadyTime}ms`);
    console.log(`Render Time: ${renderTime}ms`);

    // Resource timing
    const resources = performance.getEntriesByType('resource');
    const jsFiles = resources.filter(r => r.name.endsWith('.js'));
    const cssFiles = resources.filter(r => r.name.endsWith('.css'));
    const images = resources.filter(r => r.name.match(/\.(png|jpg|jpeg|svg)$/));

    console.log(`JS Files Loaded: ${jsFiles.length} (${jsFiles.reduce((a, b) => a + b.duration, 0).toFixed(2)}ms total)`);
    console.log(`CSS Files Loaded: ${cssFiles.length} (${cssFiles.reduce((a, b) => a + b.duration, 0).toFixed(2)}ms total)`);
    console.log(`Images Loaded: ${images.length} (${images.reduce((a, b) => a + b.duration, 0).toFixed(2)}ms total)`);

    // Find slow resources
    const slowResources = resources.filter(r => r.duration > 100).sort((a, b) => b.duration - a.duration);
    if (slowResources.length > 0) {
      console.log('=== Slow Resources (>100ms) ===');
      slowResources.slice(0, 5).forEach(r => {
        console.log(`${r.name.split('/').pop()}: ${r.duration.toFixed(2)}ms`);
      });
    }
  });
})();

