/* Critical Combined CSS - Combines essential styles for faster initial render */

/* Import critical styles in order of importance */
@import url('poc-layout.css');
@import url('canvas-view.css');
@import url('bureau-logos.css');
@import url('creditor-logo.css');
@import url('evidence-buttons.css');
@import url('dim-overlay.css');

/* Critical inline styles to prevent FOUC */
body {
  margin: 0;
  padding: 0;
  width: 1920px;
  height: 1080px;
  background: linear-gradient(to bottom, #d8d8d8, #bfc3c8);
}

/* Prevent layout shift for critical elements */
#Image_Canvas_Container {
  position: absolute;
  top: 140px;
  left: 15px;
  width: 810px;
  height: 920px;
  overflow: hidden;
}

#Report_Image {
  width: 100%;
  height: 100%;
  object-fit: contain;
}

/* Ensure navigation arrows don't cause layout shift */
.canvas-arrow {
  opacity: 0;
  transition: opacity 0.2s ease-in-out;
}

.canvas-arrow.loaded {
  opacity: 1;
}

