// performance-optimizations.js
// Enhanced performance optimizations for faster page load

(function() {
  'use strict';

  // Defer non-critical resource loading
  window.deferredResources = {
    scripts: [],
    styles: [],
    images: []
  };

  // Mark critical functions that need immediate loading
  window.criticalFunctions = [
    'setCreditorLogo',
    'setBureauLogos', 
    'loadCanvasImage',
    'setCanvasHeading'
  ];

  // Lazy load JSON data only when needed
  window.lazyLoadJSON = async function(url, callback) {
    if (window.jsonCache && window.jsonCache[url]) {
      callback(window.jsonCache[url]);
      return;
    }

    try {
      const response = await fetch(url);
      const data = await response.json();

      // Cache the data
      window.jsonCache = window.jsonCache || {};
      window.jsonCache[url] = data;

      callback(data);
    } catch (error) {
      console.error('Error loading JSON:', url, error);
      callback(null);
    }
  };

  // Defer loading of large JSON files
  window.deferJSONLoad = function() {
    // Remove automatic loading from evidence-actual.js and matters-render.js
    window.evidenceDocumentsLoaded = false;
    window.mattersContentLoaded = false;
  };

  // Optimize image loading with intersection observer
  window.lazyLoadImages = function() {
    const imageObserver = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const img = entry.target;
          if (img.dataset.src) {
            img.src = img.dataset.src;
            img.removeAttribute('data-src');
            observer.unobserve(img);
          }
        }
      });
    });

    document.querySelectorAll('img[data-src]').forEach(img => {
      imageObserver.observe(img);
    });
  };

  // Batch DOM updates
  window.batchDOMUpdates = function(updates) {
    requestAnimationFrame(() => {
      updates.forEach(update => update());
    });
  };

  // Defer non-critical CSS - removed as it's already handled in HTML
  window.deferStyles = function() {
    // Styles are already deferred in HTML with media="print" attribute
    return;
  };

  // Initialize performance optimizations
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', function() {
      window.deferJSONLoad();
      window.deferStyles();
      window.lazyLoadImages();
    });
  } else {
    window.deferJSONLoad();
    window.deferStyles();
    window.lazyLoadImages();
  }

})();

