391	    pointer-events: none;
392	    user-select: none;
393	    line-height: 1;
394	    width: 20px;
395	    height: 11px;
396	    left: 3px; /* OFF state: x282 - container x279 = 3px */
397	    top: 6px; /* y141 - container y135 = 6px */
398	    display: flex;
399	    align-items: center;
400	    justify-content: center;
401	    transition: all 0.15s ease; /* Match indicator transition timing */
402	}
