/* ========================================
   Enhanced Sidebar Top Stack Components
   ======================================== */

/* Main wrapper for the stacked components */
.sidebar-top-stack {
    display: flex;
    flex-direction: column;
    gap: 0; /* No gap between components */
    width: 100%;
    position: relative;
    z-index: 1; /* Keep below heading */
}

/* ========================================
   Filter Counter Component
   ======================================== */
.filter-counter {
    display: flex;
    flex-direction: column;
    width: 100%;
}

/* Header container - always visible */
.filter-counter-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 24px 0 24px;
    margin: 0; /* No negative margin needed */
    position: relative;
    z-index: 1; /* Lower than heading to prevent overlap */
}

.filter-counter-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 26pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-counter-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
    position: relative;
    z-index: 9999; /* Bring to front without affecting layout */
}

.filter-counter-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #253541 !important;
    stroke-opacity: 1 !important; /* 100% opacity for default state */
    fill: none !important;
    transition: all 200ms ease;
    position: relative;
    z-index: 9999;
}

.filter-counter-toggle:hover svg {
    stroke: #F26419 !important;
    stroke-opacity: 1 !important; /* 100% opacity on hover */
}

/* Collapsible content container */
.filter-counter-content {
    overflow: hidden;
    transition: all 300ms ease;
    transform-origin: top;
}

.filter-counter.collapsed .filter-counter-content {
    height: 0;
    opacity: 0;
    transform: scaleY(0);
}

/* Intro text */
.filter-counter-intro {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 9pt;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.4;
    padding: 8px 24px;
    margin: 0; /* No negative margin needed */
    max-width: 100%;
}

/* Filter options container */
.filter-options-container {
    width: 390px;
    background: #D9D8D7; /* 30% tint of #B7B5B3 */
    border: 1pt solid #ffffff; /* 1pt white stroke */
    border-radius: 32px;
    padding: 8px 30px; /* Minimal top/bottom padding for container */
    margin: 18px 0 0 14px; /* Added 10px to top margin (was 8px, now 18px) */
