    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 13px;
    color: rgba(37, 53, 65, 0.9); /* #253541 at 90% opacity */
    line-height: 1.2;
    user-select: none;
}

/* Special layout for "selected violations" - spans full width */
.filter-option:first-child {
    grid-column: 1 / -1;
}

