/* ========================================
   Tool Tips Toggle
   ======================================== */
.tool-tips-toggle {
    position: fixed;
    top: 135px;
    left: 279px;
    width: 54px;
    height: 27.5px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
