    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 24pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-counter-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
    position: relative;
    z-index: 9999; /* Bring to front without affecting layout */
}

.filter-counter-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #253541 !important;
    stroke-opacity: 1 !important; /* 100% opacity for default state */
    fill: none !important;
    transition: all 200ms ease;
    position: relative;
    z-index: 9999;
}

.filter-counter-toggle:hover svg {
    stroke: #F26419 !important;
    stroke-opacity: 1 !important; /* 100% opacity on hover */
}

/* Collapsible content container */
