    position: absolute;
    left: 21px; /* Adjusted for increased left padding (7px + 14px) */
    top: 50%;
    transform: translateY(-50%);
    width: 24px;
    height: 24px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.violation-box.high .violation-severity-badge {
    background-color: #FF0000;
}

.violation-box.medium .violation-severity-badge {
    background-color: #F26419;
}

.violation-box.low .violation-severity-badge {
    background-color: #F2B919;
}

.violation-severity-badge svg {
    width: 16px;
    height: 16px;
    stroke: #ffffff !important;
    fill: none !important;
}

/* Override global Lucide styling for severity badges */
.violation-severity-badge [data-lucide] {
    stroke: #ffffff !important;
}

/* Violation content */
.violation-content {
    flex: 1;
    padding-left: 30px; /* Space for badge */
}

/* FCRA codes */
