.violation-details-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 24px 0 24px;
    margin: 0;
    position: relative;
}

.violation-details-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
