 55	            font-family: "jaf-bernino-sans-comp", sans-serif;
 56	            font-weight: 800;
 57	            font-size: 30pt;
 58	            color: #253541;
 59	            text-transform: lowercase;
 60	            line-height: 1;
 61	        }
 62	        
 63	        .filter-counter-toggle {
 64	            width: 28px;
 65	            height: 28px;
 66	            background: transparent;
 67	            border: none;
 68	            cursor: pointer;
 69	            display: flex;
 70	            align-items: center;
 71	            justify-content: center;
 72	            border-radius: 8px;
 73	            transition: all 200ms ease;
 74	            padding: 0;
 75	        }
