 80	            stroke: #9BA1A6; /* 30% tint of #253541 */
 81	            fill: none;
 82	            transition: all 200ms ease;
 83	        }
 84	        
 85	        .filter-counter-toggle:hover svg {
 86	            stroke: #F26419;
 87	        }
