130	        .filter-option {
131	            display: flex;
132	            align-items: flex-start;
133	            gap: 8px;
134	            cursor: pointer;
135	            position: relative;
136	        }
137	        
138	        .filter-option input[type="radio"] {
139	            -webkit-appearance: none;
140	            -moz-appearance: none;
141	            appearance: none;
142	            width: 13px;
143	            height: 13px;
144	            border: 2px solid #253541;
145	            border-radius: 50%;
146	            background-color: #ffffff;
147	            margin: 2px 0 0 0; /* Small top margin to align with text baseline */
148	            cursor: pointer;
149	            flex-shrink: 0;
150	            transition: background-color 200ms ease;
151	        }
152	        
153	        .filter-option input[type="radio"]:checked {
154	            background-color: #ff0000;
155	        }
156	        
157	        .filter-option span {
158	            font-family: 'Space Grotesk', sans-serif;
159	            font-weight: 400;
160	            font-size: 14px;
161	            color: rgba(37, 53, 65, 0.9);
162	            line-height: 1.2;
163	            user-select: none;
164	        }
165	        
166	        /* Special layout for "selected violations" - spans full width */
167	        .filter-option:first-child {
168	            grid-column: 1 / -1;
169	        }
170	        
171	        /* Disabled state */
172	        .filter-option.disabled {
173	            opacity: 0.5;
174	            cursor: not-allowed;
175	        }
176	        
177	        .filter-option.disabled input[type="radio"] {
178	            cursor: not-allowed;
179	            opacity: 0.5;
180	        }
181	        
182	        .filter-option.disabled input[type="radio"]:checked {
183	            background-color: rgba(255, 0, 0, 0.5);
184	        }
185	        
186	        /* Tooltip for disabled options */
187	        .filter-option.disabled::after {
188	            content: attr(data-tooltip);
189	            position: absolute;
190	            bottom: 100%;
191	            left: 50%;
192	            transform: translateX(-50%);
193	            background: #253541;
194	            color: white;
195	            font-size: 12px;
196	            padding: 6px 12px;
197	            border-radius: 6px;
198	            white-space: nowrap;
199	            opacity: 0;
200	            pointer-events: none;
201	            transition: opacity 200ms ease;
202	            margin-bottom: 8px;
203	            z-index: 10;
204	        }
205	        
206	        .filter-option.disabled:hover::after {
207	            opacity: 1;
208	        }
209	        
210	        /* Placeholder content below */
211	        .placeholder-content {
212	            margin-top: 24px;
213	            padding: 20px;
214	            background: #F3F4F6;
215	            border-radius: 16px;
216	            color: #64748B;
217	            text-align: center;
218	        }
219	    </style>
220	</head>
221	<body>
222	    <div class="test-container">
223	        <!-- Filter Counter Component -->
224	        <div class="filter-counter" id="filterCounter">
225	            <!-- Header - Always Visible -->
226	            <div class="filter-counter-header">
227	                <h3 class="filter-counter-title">filter this report</h3>
228	                <button class="filter-counter-toggle" aria-label="Toggle filter section" aria-expanded="true">
229	                    <i data-lucide="circle-arrow-up"></i>
230	                </button>
231	            </div>
232	            
233	            <!-- Collapsible Content -->
234	            <div class="filter-counter-content">
235	                <!-- Intro Text -->
236	                <p class="filter-counter-intro">
237	                    Select one or more filters below to customize the report view. Your selections affect which severity data and violations are shown.
238	                </p>
239	                
240	                <!-- Filter Options Container -->
241	                <div class="filter-options-container">
242	                    <label class="filter-option">
243	                        <input type="radio" name="violation-filter" value="selected">
244	                        <span>selected violations</span>
245	                    </label>
246	                    <label class="filter-option">
247	                        <input type="radio" name="violation-filter" value="total" checked>
248	                        <span>total violations (all pages)</span>
249	                    </label>
250	                    <label class="filter-option">
251	                        <input type="radio" name="violation-filter" value="page">
252	                        <span>current page only</span>
253	                    </label>
254	                </div>
255	            </div>
256	        </div>
257	        
258	        <!-- Placeholder content to show stacking behavior -->
259	        <div class="placeholder-content">
260	            <p>Severity boxes component would go here</p>
261	            <p>Notice how this shifts up when filter collapses</p>
262	        </div>
263	    </div>
264	    
265	    <script>
266	        // Initialize Lucide icons
267	        lucide.createIcons();
268	        
269	        // Filter Counter functionality
270	        const filterCounter = document.getElementById('filterCounter');
271	        const toggleButton = filterCounter.querySelector('.filter-counter-toggle');
272	        const filterOptions = filterCounter.querySelectorAll('input[name="violation-filter"]');
273	        
274	        // Toggle collapse/expand
275	        toggleButton.addEventListener('click', () => {
276	            filterCounter.classList.toggle('collapsed');
277	            const isCollapsed = filterCounter.classList.contains('collapsed');
278	            toggleButton.setAttribute('aria-expanded', !isCollapsed);
279	            
280	            // Update icon - when expanded show up arrow, when collapsed show down arrow
281	            const icon = toggleButton.querySelector('i');
282	            if (isCollapsed) {
283	                icon.setAttribute('data-lucide', 'circle-arrow-down');
284	            } else {
285	                icon.setAttribute('data-lucide', 'circle-arrow-up');
286	            }
287	            lucide.createIcons();
288	        });
289	        
290	        // Handle filter changes with mutual exclusivity
291	        filterOptions.forEach(option => {
292	            option.addEventListener('change', (e) => {
293	                const value = e.target.value;
294	                
295	                // Reset all disabled states
296	                filterOptions.forEach(opt => {
297	                    const label = opt.parentElement;
298	                    label.classList.remove('disabled');
299	                    label.removeAttribute('data-tooltip');
300	                    opt.disabled = false;
301	                });
302	                
303	                // Apply mutual exclusivity rules
304	                if (value === 'total') {
305	                    // Disable "current page only"
306	                    const pageOption = document.querySelector('input[value="page"]');
307	                    if (pageOption) {
308	                        const label = pageOption.parentElement;
309	                        label.classList.add('disabled');
310	                        label.setAttribute('data-tooltip', 'Cannot combine with "total violations"');
311	                        pageOption.disabled = true;
312	                    }
313	                } else if (value === 'page') {
314	                    // Disable "total violations"
315	                    const totalOption = document.querySelector('input[value="total"]');
316	                    if (totalOption) {
317	                        const label = totalOption.parentElement;
318	                        label.classList.add('disabled');
319	                        label.setAttribute('data-tooltip', 'Cannot combine with "current page only"');
320	                        totalOption.disabled = true;
321	                    }
322	                }
323	            });
324	        });
325	        
326	        // Trigger initial state
327	        const checkedOption = document.querySelector('input[name="violation-filter"]:checked');
328	        if (checkedOption) {
329	            checkedOption.dispatchEvent(new Event('change'));
330	        }
331	    </script>
332	</body>
333	</html>

