   ======================================== */
.view-toggle-new {
    position: fixed;
    top: 76px;
    left: 70px;
    width: 270px;
    height: 40px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
    overflow: visible;
    z-index: 100; /* High z-index to ensure clickability */
}

/* View labels container */
