renderViolations() {
    const container = document.querySelector('.viobox-container');
    if (!container) return;

    // Clear existing boxes
    container.innerHTML = '';

    // Get violations for current page
    const currentPageKey = `${this.currentCreditor}-${this.currentBureau}-${this.reportDate}-P${String(this.currentPage).padStart(2, '0')}`;

    if (this.violationsData && this.violationsData.byPage && this.violationsData.byPage[currentPageKey]) {
        this.violations = this.violationsData.byPage[currentPageKey];
    } else if (this.violationsData && this.violationsData.violations) {
        // Filter violations for current page from flat list
        this.violations = this.violationsData.violations.filter(v => 
            v.creditor === this.currentCreditor &&
            v.bureau === this.currentBureau &&
            v.date === this.reportDate &&
            v.page === this.currentPage
        );
    } else {
        this.violations = [];
    }

    // Render each violation
    this.violations.forEach(violation => {
        const viobox = this.createVIObox(violation);
        container.appendChild(viobox);
    });

    // Update sidebar
    this.updateViolationSidebar();
}

createVIObox(violation) {
    const box = document.createElement('button');
    box.className = `viobox ${violation.severity}`;
    box.setAttribute('role', 'button');
    box.setAttribute('aria-label', `${violation.severity} severity violation: ${violation.description || violation.text || ''}`);
    box.setAttribute('tabindex', '0');
    box.setAttribute('data-violation-id', violation.id);
    box.style.left = `${violation.x}px`;
    box.style.top = `${violation.y}px`;

    // Set custom width/height if provided
    if (violation.width) {
        box.style.width = `${violation.width}px`;
    }
    if (violation.height) {
        box.style.height = `${violation.height}px`;
    }

    // Add severity icon
    const icon = document.createElement('div');
    icon.className = `severity-icon severity-${violation.severity}`;

    const iconEl = document.createElement('i');
    const iconMap = {
        high: 'shield-alert',
        medium: 'triangle-alert',
        low: 'circle-alert'
    };
    iconEl.setAttribute('data-lucide', iconMap[violation.severity]);
    icon.appendChild(iconEl);

    box.appendChild(icon);

    // Add tooltip for violation text
    if (this.tipsEnabled) {
        const tooltip = document.createElement('span');
        tooltip.className = 'viobox-tooltip';
        tooltip.textContent = violation.text;
        box.appendChild(tooltip);
    }

    // Add click handler
    box.addEventListener('click', (e) => {
        e.preventDefault();
        this.selectViolation(violation, e.currentTarget);
    });

    return box;
}

selectViolation(violation, viobox) {
    const isSelected = this.selectedViolations.has(violation.id);

