        let newIndex = currentIndex + direction;

        // Handle wrap around
        if (newIndex < 0) newIndex = pages.length - 1;
        if (newIndex >= pages.length) newIndex = 0;

        const nextPage = pages[newIndex];
        tooltip.textContent = `page ${nextPage}`;
    }
