}

.content-wrapper {
    position: relative;
    width: 100%;
    height: 100%;
}

/* ========================================
   Report Canvas
   ======================================== */
.report-canvas-container,
.tagger-canvas-container,
.evidence-canvas-container {
    position: fixed;
    left: 360px;
    top: 69px;
    width: 810px;
    height: 920px;
    background-color: #ffffff;
    border-radius: 24px;
