216	            document.body.classList.toggle('tooltip-mode', this.tipsEnabled);
217	        }
218	        
219	        this.announce(`Tooltips ${this.tipsEnabled ? 'enabled' : 'disabled'}`);
220	    }
221	    
222	    navigatePage(direction) {
223	        const newPage = this.currentPage + direction;
224	        if (newPage >= 1 && newPage <= this.totalPages) {
225	            this.goToPage(newPage);
226	        }
227	    }
228	    
229	    goToPage(pageNum) {
230	        if (pageNum < 1 || pageNum > this.totalPages || isNaN(pageNum)) {
231	            return;
232	        }
233	        
234	        this.currentPage = pageNum;
235	        const pageInput = document.querySelector('.page-input');
236	        if (pageInput) {
237	            pageInput.value = pageNum;
