}

/* ========================================
   New View Toggle
   ======================================== */
.view-toggle-new {
    position: fixed;
    top: 76px;
    left: 70px;
    width: 270px;
    height: 40px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
