    z-index: 100;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Tips indicator (sliding button) */
.tips-indicator {
    position: absolute;
    width: 27.4px;
    height: 27.4px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
