}

/* Vertical dividers */
.nav-divider {
    position: absolute;
    width: 1px;
    height: 18px;
    background-color: #9ba1a6;
    top: 50%;
    transform: translateY(-50%);
    z-index: 10; /* Higher than nav-bar z-index of 2 */
}

/* Report nav dividers - using specific classes for precise control */
