    isolation: isolate; /* Create new stacking context */
}

/* Bottom box (base layer) */
.sidebar-bottom-box {
    position: absolute;
    width: 452px;
    height: 920px;
    background-color: #253541;
    border: 16px solid #253541;
    border-radius: 24px;
    box-sizing: border-box;
    box-shadow: 0 8px 16px rgba(255, 255, 255, 0.4);
}

/* Top box (content layer) */
.sidebar-top-box {
    position: absolute;
    left: 16px; /* 1196px - 1180px (sidebar-canvas position) */
    top: 57px; /* 126px - 69px (sidebar-canvas top position) */
    width: 419px;
    height: 848px;
    background-color: #ffffff;
    border: none;
    border-radius: 24px;
    overflow: visible;
    padding: 0; /* No padding */
    box-sizing: border-box;
}

/* Variable heading - positioned above both boxes */
.sidebar-variable-heading {
    position: absolute;
    left: 40px; /* 1220px - 1180px container position */
    top: -28px; /* Positioned above the sidebar-canvas */
    width: 374px;
    height: 39px;
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
    font-size: 32pt;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
    z-index: 10000; /* Highest z-index to ensure visibility above all elements */
}

/* FCRA stays uppercase */
.sidebar-variable-heading .uppercase {
    text-transform: uppercase;
}

/* Old sidebar styles - to be removed after HTML update */
.violation-sidebar, .tagger-sidebar, .evidence-sidebar {
    display: none;
}

.sidebar-heading {
    font-size: 24px;
    font-weight: var(--font-bold);
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
    box-shadow: var(--shadow-sm);
}

.stat-icon {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon i {
    width: 20px;
    height: 20px;
    color: #ffffff;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    color: var(--text-secondary);
    letter-spacing: 0;
}

.stat-value {
    font-size: 32px;
    font-weight: var(--font-bold);
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
