toggleDocket() {
    this.docketExpanded = !this.docketExpanded;
    const header = document.querySelector('.docket-header');
    const arrowCollapsed = document.querySelector('.arrow-collapsed');
    const upToggle = document.querySelector('.docket-toggle');

    if (header && arrowCollapsed) {
        if (this.docketExpanded) {
            // Expand - show header, hide collapsed arrow (which shows up arrow)
            header.classList.remove('collapsed');
            arrowCollapsed.classList.remove('visible');
            arrowCollapsed.setAttribute('aria-expanded', 'false');
            if (upToggle) upToggle.setAttribute('aria-expanded', 'true');

            // Change icon to circle-chevron-up
            const arrowIcon = arrowCollapsed.querySelector('i');
            if (arrowIcon) {
                arrowIcon.setAttribute('data-lucide', 'circle-chevron-up');
            }
        } else {
            // Collapse - hide header, show collapsed arrow with down icon
            header.classList.add('collapsed');
            arrowCollapsed.classList.add('visible');
            arrowCollapsed.setAttribute('aria-expanded', 'true');
            if (upToggle) upToggle.setAttribute('aria-expanded', 'false');

            // Change icon to circle-chevron-down
            const arrowIcon = arrowCollapsed.querySelector('i');
            if (arrowIcon) {
                arrowIcon.setAttribute('data-lucide', 'circle-chevron-down');
            }
        }

        // Update icons using Lucide
        lucide.createIcons();
    }
}

