/* ========================================
   Layout Structure
   ======================================== */
.vioverse-app {
    position: relative;
    min-width: 1920px;
    min-height: 100vh;
    margin: 0 auto;
    background-color: #4f5a63;
    overflow: visible;
    z-index: 0;
}

/* ========================================
   Docket Header
   ======================================== */
.docket-header {
    position: fixed;
    top: 9px;
    left: 11px;
    width: 1898px;
    height: 48px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
