/* Independent Collapsible Button - Not linked to docket */
.custom-collapse-container {
    position: fixed;
    left: 1576px;
    top: 167px;
    width: 28px;
    height: 28px;
    z-index: 999999;
}

.custom-collapse-btn {
    position: absolute;
    left: 0;
    top: 0;
    width: 28px;
    height: 28px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.15s ease;
}

.custom-collapse-btn svg {
    width: 28px;
    height: 28px;
    stroke: #253541;
    fill: none;
    stroke-width: 2;
}

.custom-collapse-btn:hover svg {
    stroke: #f26419;
}

.custom-collapse-btn[aria-expanded="true"] svg {
    stroke: #253541;
}

/* Down arrow when collapsed - for docket header */
.arrow-collapsed {
    position: fixed;
    top: 18px; /* 9px + 9px to center in the 48px space */
    left: 38px; /* 11px + 27px to maintain same x position */
    width: 28px;
    height: 28px;
    background: none;
    border: none;
    cursor: pointer;
    color: #9ba1a6;
    padding: 0;
    display: none;
    align-items: center;
    justify-content: center;
    transition: color var(--transition-fast);
    z-index: calc(var(--z-base) + 1);
}

.arrow-collapsed.visible {
    display: flex;
}

.arrow-collapsed:hover {
    color: #f26419;
}

.arrow-collapsed svg {
    width: 28px;
    height: 28px;
}

.docket-content {
    display: flex;
    align-items: center;
    gap: 24px;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
