    align-items: center;
}

.nav-value {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
    display: inline-flex;
    align-items: center;
}

/* Creditor name specific truncation */
.creditor-segment .nav-value {
    overflow: hidden;
    text-overflow: ellipsis;
