/* Severity label */
.severity-label {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 16px;
    color: rgba(37, 53, 65, 0.9);
    text-transform: lowercase;
    width: 110px;
    text-align: center;
    line-height: 1.2;
    margin-bottom: 8px;
    word-break: break-word; /* Force text to break between words */
    white-space: pre-line; /* Respect line breaks */
}

/* Severity badge */
.severity-badge {
    width: 46px;
    height: 46px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    left: 26px; /* Center horizontally */
    top: 50px;
    padding: 3px; /* Add 3px padding */
    box-sizing: border-box; /* Include padding in width/height */
}

.severity-box.high .severity-badge {
    background-color: #FF0000;
}

.severity-box.medium .severity-badge {
    background-color: #F26419;
}

.severity-box.low .severity-badge {
    background-color: #F2B919;
}

.severity-badge svg {
    width: 24px;
    height: 24px;
    stroke: #ffffff !important;
    fill: none !important;
}

/* Override global Lucide styling for severity badges */
.severity-badge [data-lucide] {
    stroke: #ffffff !important;
}

/* Severity count */
.severity-count {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
    font-size: 52px;
    color: rgba(37, 53, 65, 0.9);
    position: absolute;
    right: 15px;
    bottom: 10px;
    line-height: 1;
    width: 36px;
    text-align: center;
}

/* Dynamic adjustment for 2-3 digit numbers */
.severity-box.has-double-digits .severity-badge {
    left: 20px; /* Shift left */
}

.severity-box.has-triple-digits .severity-badge {
    left: 14px; /* Shift more left */
}

.severity-box.has-double-digits .severity-count,
.severity-box.has-triple-digits .severity-count {
    width: auto;
    right: 10px;
}

