    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 19.2px; /* 20% smaller: 24px * 0.8 */
    height: 19.2px; /* 20% smaller: 24px * 0.8 */
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: none;
